import React, { useContext } from "react";
import { ToggleTheme } from "../App";

function UseContext() {
  const theme = useContext(ToggleTheme);
  const themeStyle = {
    backgroundColor: theme ? "black" : "grey",
    color: theme ? "grey" : "black",
    padding: "2rem",
    margin: "2rem",
  };

  function content() {
    document.getElementById("para").classList.toggle("active");
  }

  return (
    <>
      <div id="para" style={themeStyle}>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Tenetur nobis,
        soluta labore eveniet minima adipisci asperiores voluptatum vel
        veritatis! Mollitia corrupti laboriosam, molestiae eligendi soluta
        eveniet est iure ab rerum error perspiciatis facere quidem incidunt
        ipsum possimus vero nostrum voluptates tempora suscipit? Perspiciatis
        mollitia accusantium quasi necessitatibus quis sed aspernatur, veritatis
        rerum neque temporibus vitae. Fugiat iste, officiis modi harum molestias
        quia ullam inventore consectetur dicta qui earum, cum dolorem voluptate
        necessitatibus minima praesentium ad quod repellat nisi maiores quasi
        tenetur vel deserunt libero? Iste laboriosam nisi nihil autem omnis.
        <button onClick={content}>Content</button>
      </div>
    </>
  );
}

export default UseContext;
